settings.code:
{% style %}
.main_search_label {
    position: relative;
    text-align: left;
    width: 100%;
}

.distance_label,
.search_limit_label,
#filters_text,
#submitBtn {
    position: relative;
    display: flex;
    align-items: center;
    min-width: 80px;
    text-align: left;
    justify-content: flex-end;
    white-space: nowrap;
}

button#submitBtn {
    justify-content: center;
}

.pac-target-input::placeholder {
    color: transparent; 
}

@media (max-width: 800px) {
    .main_search_label {
        height: 50px;
    }

    .distance_label::after,
    .search_limit_label::after {
        left: 0;
    }
}

{% if request.locale.iso_code == 'de' %}
.main_search_label,
.distance_label,
.search_limit_label,
#filters_text,
#submitBtn,
label[for="search_filter_17635"],
label[for="search_filter_17640"] {
    color: transparent !important; 
    white-space: nowrap;
}

label[for="search_filter_17640"],
label[for="search_filter_17635"] {
    position: relative;
    display: flex;
}

.main_search_label::after {
    content: "Finden Sie Standorte in der Nähe dieses Standorts";
    visibility: visible;
    color: initial;
    position: absolute;
    left: 0;
}

.distance_label::after {
    content: "Distanz";
    visibility: visible;
    color: initial;
    position: absolute;
    right: 0;
}

.search_limit_label::after {
    content: "Ergebnisse";
    color: initial;
    position: absolute;
    right: 0;
}

#filters_text::after {
    content: "Such-Filter";
    color: initial;
    position: absolute;
    left: 0;
}

#submitBtn::after {
    content: "Suchen";
    color: white;
    position: absolute;
    margin: 0 auto;
}

label[for="search_filter_17640"]::after {
    content: "Händler"; 
    color: initial;
    position: absolute;
    left: 20px;
    margin-top: -25px;
}

label[for="search_filter_17635"]::after {
    content: "Servicezentrum"; 
    color: initial;
    position: absolute;
    left: 20px;
    margin-top: -25px;
}
{% elsif request.locale.iso_code == 'fr' %}
