position: relative;
text-align: left;
    width: 100%;
}
.distance_label,.search_limit_label,#filters_text,#submitBtn  {
    position: relative;
display: flex;
    align-items: center;
min-width:80px;
    text-align: left;
justify-content: flex-end;
white-space: nowrap;
}
button#submitBtn {
    justify-content: center;
}
.pac-target-input::placeholder {
    color: transparent; 
}
@media (max-width: 800px) {
  .main_search_label {
      height: 50px;
}
.distance_label::after,   .search_limit_label::after {
      left: 0;
}
}
